$artworks = @(@{"id" = "438014";"filename" = "japanese_bridge.jpg";"title" = "The Japanese Bridge"},@{"id" = "436947";"filename" = "bridge_argenteuil.jpg";"title" = "Bridge at Argenteuil"},@{"id" = "438815";"filename" = "luncheon_boating.jpg";"title" = "Luncheon of the Boating Party"});$outputPath = "wwwroot/images/artworks";if (-not (Test-Path $outputPath)) {New-Item -ItemType Directory -Path $outputPath -Force};foreach ($artwork in $artworks) {$objectUrl = "https://collectionapi.metmuseum.org/public/collection/v1/objects/$($artwork.id)";Write-Host "Downloading $($artwork.title)...";try {$response = Invoke-RestMethod -Uri $objectUrl;if ($response.primaryImage) {$imageUrl = $response.primaryImage;$outputFile = Join-Path $outputPath $artwork.filename;Invoke-WebRequest -Uri $imageUrl -OutFile $outputFile;Write-Host "Successfully downloaded $($artwork.filename)"} else {Write-Host "No image found for $($artwork.title)"}} catch {Write-Host "Error downloading $($artwork.title): $_"};Start-Sleep -Seconds 1}
